import React, { useState } from 'react';
import styles from './AddUser.module.css'; // Import CSS module

const AddUser = () => {
  const [activeTab, setActiveTab] = useState('admin');

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission logic here
  };

  return (
    <div className={styles.main}>
      <div className={styles.info}>
      <div className={styles.tabs}>
        <button onClick={() => handleTabChange('admin')} className={activeTab === 'admin' ? styles.active : ''}>Admin</button>
        <button onClick={() => handleTabChange('mentor')} className={activeTab === 'mentor' ? styles.active : ''}>Mentor</button>
        <button onClick={() => handleTabChange('student')} className={activeTab === 'student' ? styles.active : ''}>Student</button>
      </div>
      <div className={styles.tabContent}>
        {activeTab === 'admin' && (
          <form onSubmit={handleSubmit}>
            <h1>Create Admin</h1>
            <label>
              Admin Name:
              <input type="text" name="adminName" />
            </label>
            <button type="submit">Submit</button>
          </form>
        )}
        {activeTab === 'mentor' && (
          <form onSubmit={handleSubmit}>
            <h1>Create Mentor</h1>
            <label>
              Mentor Name:
              <input type="text" name="mentorName" />
            </label>
            <button type="submit">Submit</button>
          </form>
        )}
        {activeTab === 'student' && (
          <form onSubmit={handleSubmit}>
            <h1>Create Student</h1>
            <label>
              First Name:
              <input type="text" name="first_name" />
            </label>
            <label>
              Middle Name:
              <input type="text" name="middle_name" />
            </label>
            <label>
              Last Name:
              <input type="text" name="last_name" />
            </label>
            <label>
              Sex:
              <input type="text" name="sex" />
            </label>
            <label>
              Student Phone Number:
              <input type="text" name="studentName" />
            </label>
            <button type="submit">Submit</button>
          </form>
        )}
      </div>
    </div>
    </div>
  );
}

export default AddUser;
