import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LoginPage from './LoginPage';
import { SidebarProvider } from './SidebarContext'; // Import your SidebarProvider
import { useSidebar } from './SidebarContext'; // Import your useSidebar hook
import StudentHome from './student/StudentHome';
import AdminHome from './admin/AdminHome/AdminHome';
import MentorHome from './mentor/MentorHome';
import StudentSidebar from './student/StudentSidebar';
import StudentProfile from './student/StudentProfile';
import Progress from './student/Progress';
import StudentProjects from './student/StudentProjects';
import Achievements from './student/Achievements';
import Header from './Header';
import './App.css';
import Layout from './Layout';
import AddUser from './admin/AddUser/AddUser';
import UpdateUser from './admin/UpdateUser/UpdateUser';
import DeleteUser from './admin/DeleteUser/DeleteUser';
import AdminSidebar from './Sidebar/AdminSidebar/AdminSidebar';

function App() {
  return (
    <Router>
      <SidebarProvider> {/* Wrap your app with the SidebarProvider */}
        <AppContent />
      </SidebarProvider>
    </Router>
  );
}

function AppContent() {
  const { isSidebarOpen } = useSidebar(); // Use the useSidebar hook to access the sidebar state

  return (
    <div>
      {/* Include the Header component */}
      <Header />
      <Routes>
        <Route exact path="/" element={<LoginPage />} />
        <Route path="/home/*" element={<Home />} />
        <Route path="/student" element={<StudentRoutes />} />
        <Route path="/admin" element={<AdminRoutes />} />
      </Routes>
    </div>
  );
}

function Home() {
  // Your existing Home component code
}

function StudentRoutes() {
  // Your existing StudentRoutes component code
}

function AdminRoutes() {
  // Your existing AdminRoutes component code
}

export default App;
