import React from 'react';
import { Link } from 'react-router-dom';

const AdminSidebar = () => {
    const [activeTab, setActiveTab] = useState(null); // State to manage active tab

  // Function to handle click event on tab
  const handleTabClick = (tabName) => {
    setActiveTab(tabName);
  };
  return (
    <nav className='adminDash'>
      <ul className="dashLinks">
        <li className="link">
        <Link to='/admin/home' className={activeTab === 'home' ? 'active' : ''} onClick={() => handleTabClick('home')}>
            <span className="material-symbols-outlined">home</span>
            Home
          </Link>
        </li>
        <li className="link">
          <Link to='/admin/AddUser' className={activeTab === 'AddUser' ? 'active' : ''} onClick={() => handleTabClick('AddUSer')}>
            <span className="material-symbols-outlined">person_add</span>
            Add User
          </Link>
        </li>
        <li className="link">
          <Link to='/admin/UpdateUser' className={activeTab === 'UpdateUser' ? 'active' : ''} onClick={() => handleTabClick('UpdateUser')}>
            <span className="material-symbols-outlined">manage_accounts</span>
            Update User
          </Link>
        </li>
        <li className="link">
          <Link to='/admin/DeleteUser' className={activeTab === '' ? 'active' : ''} onClick={() => handleTabClick('DeleteUser')}>
            <span className="material-symbols-outlined">person_remove</span>
            Delete User
          </Link>
        </li>
      </ul>
    </nav>
  );
}

export default AdminSidebar;
