import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import LoginPage from './LoginPage';
import StudentHome from './student/StudentHome';
import AdminHome from './admin/AdminHome/AdminHome';
import MentorHome from './mentor/MentorHome';
import StudentSidebar from './student/StudentSidebar';
import StudentProfile from './student/StudentProfile';
import Progress from './student/Progress';
import StudentProjects from './student/StudentProjects';
import Achievements from './student/Achievements';
import Header from './Header';
import './App.css';
import Layout from './Layout';
import AddUser from './admin/AddUser/AddUser';
import UpdateUser from './admin/UpdateUser/UpdateUser';
import DeleteUser from './admin/DeleteUser/DeleteUser';
import AdminSidebar from './Sidebar/AdminSidebar/AdminSidebar';

function App() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // State to manage sidebar visibility

  const toggleSidebar = () => {
    setIsSidebarOpen(prevState => !prevState);
  };

  return (
    <Router>
      <div>
        {/* Include the Header component */}
        <Header toggleSidebar={toggleSidebar} />
        <Routes>
          <Route exact path="/" element={<LoginPage />} />
          <Route path="/home/*" element={<Home />} />
          <Route path="/student" element={<StudentRoutes />} />
          <Route path="/admin" element={<AdminRoutes />} />
        </Routes>
      </div>
    </Router>
  );

  function Home() {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const userRole = searchParams.get('role') || 'guest'; // Default role if not provided

    switch (userRole) {
      case 'student':
        return (
          <div className='studentPage'>
            <Layout
              sidebar={<StudentSidebar />}
              childComponent={<StudentHome />}
              isSidebarOpen={isSidebarOpen}
            />
          </div>
        );
      case 'admin':
        return (
          <div className='adminPage'>
            <Layout
              sidebar={<AdminSidebar />}
              childComponent={<AdminHome />}
              isSidebarOpen={isSidebarOpen}
            />
          </div>
        );
      case 'mentor':
        return <MentorHome />;
      default:
        return <LoginPage />;
    }
  }

  function StudentRoutes() {
    return (
      <>
        <Route path='home' element={<Layout sidebar={<StudentSidebar />} childComponent={<StudentHome />} isSidebarOpen={isSidebarOpen} />} />
        <Route path='profile' element={<Layout sidebar={<StudentSidebar />} childComponent={<StudentProfile />} isSidebarOpen={isSidebarOpen} />} />
        <Route path='progress' element={<Layout sidebar={<StudentSidebar />} childComponent={<Progress />} isSidebarOpen={isSidebarOpen} />} />
        <Route path='projects' element={<Layout sidebar={<StudentSidebar />} childComponent={<StudentProjects />} isSidebarOpen={isSidebarOpen} />} />
        <Route path='achievements' element={<Layout sidebar={<StudentSidebar />} childComponent={<Achievements />} isSidebarOpen={isSidebarOpen} />} />
      </>
    );
  }

  function AdminRoutes() {
    return (
      <>
        <Route path='home' element={<Layout sidebar={<AdminSidebar />} childComponent={<AdminHome />} isSidebarOpen={isSidebarOpen} />} />
        <Route path='addUser' element={<Layout sidebar={<AdminSidebar />} childComponent={<AddUser />} isSidebarOpen={isSidebarOpen} />} />
        <Route path='updateUser' element={<Layout sidebar={<AdminSidebar />} childComponent={<UpdateUser />} isSidebarOpen={isSidebarOpen} />} />
        <Route path='deleteUser' element={<Layout sidebar={<AdminSidebar />} childComponent={<DeleteUser />} isSidebarOpen={isSidebarOpen} />} />
      </>
    );
  }
}

export default App;
