import React, { useState } from 'react';
import axios from 'axios'; // Import Axios
import styles from './AddUser.module.css'; // Import CSS module

const AddUser = () => {
  const [activeTab, setActiveTab] = useState('admin');
  const [formData, setFormData] = useState({
    admin: {
      role_type: 'admin',
      admin_fname: '',
      admin_mname: '',
      admin_lname: '',
      email: '',
    },
    mentor: {
      role_type: 'mentor',
      mentor_fname: '',
      mentor_mname: '',
      mentor_lname: '',
      email: '',
      dept_id: '',
      qualification: '',
    },
    student: {
      role_type: 'student',
      student_fname: '',
      student_mname: '',
      student_lname: '',
      sex: 'male',
      semester: '1',
      student_phone_no: '',
      email: '',
      dob: '',
      address: '',
      dept_id: '',
      guardian_name: '',
      guardian_phone_no: '',
    },
  });

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [activeTab]: {
        ...formData[activeTab],
        [name]: value,
      },
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/v1/admin/createUser', formData[activeTab]);
      console.log('User created:', response.data);
      // Reset form data after successful submission
      setFormData({
        ...formData,
        [activeTab]: {
          ...formData[activeTab],
          role_type: formData[activeTab].role_type,
          mentor_fname: '',
          mentor_mname: '',
          mentor_lname: '',
          email: '',
          dept_id: '',
          qualification: '',
          student_fname: '',
          student_mname: '',
          student_lname: '',
          sex: 'male',
          semester: '1',
          student_phone_no: '',
          email: '',
          dob: '',
          address: '',
          dept_id: '',
          guardian_name: '',
          guardian_phone_no: '',
        },
      });
    } catch (error) {
      console.error('Error creating user:', error.message);
    }
  };

  return (
    <div className={styles.main}>
      <div className={styles.info}>
        <div className={styles.tabs}>
          <button onClick={() => handleTabChange('admin')} className={activeTab === 'admin' ? styles.active : ''}>Admin</button>
          <button onClick={() => handleTabChange('mentor')} className={activeTab === 'mentor' ? styles.active : ''}>Mentor</button>
          <button onClick={() => handleTabChange('student')} className={activeTab === 'student' ? styles.active : ''}>Student</button>
        </div>
        <div className={styles.scrollableContent}>
          <div className={styles.tabContent}>
            {activeTab === 'admin' && (
              <form onSubmit={handleSubmit} className={styles.form}>
                <h1>Create Admin</h1>
                {/* Admin form fields */}
              </form>
            )}
            {activeTab === 'mentor' && (
              <form onSubmit={handleSubmit} className={styles.form}>
                <h1>Create Mentor</h1>
                {/* Mentor form fields */}
              </form>
            )}
            {activeTab === 'student' && (
              <form onSubmit={handleSubmit} className={styles.form}>
                <h1>Create Student</h1>
                {/* Student form fields */}
              </form>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default AddUser;
